"use strict";function getLeafNode(e){for(;e&&e.firstChild;){e=e.firstChild}return e}function getSiblingNode(e){for(;e;){if(e.nextSibling){return e.nextSibling}e=e.parentNode}}function getNodeForCharacterOffset(e,t){for(var o=getLeafNode(e),n=0,r=0;o;){if(3===o.nodeType){r=n+o.textContent.length;if(n<=t&&r>=t){return{node:o,offset:t-n}}n=r}o=getLeafNode(getSiblingNode(o))}}module.exports=getNodeForCharacterOffset;